import React, { useState, useEffect } from 'react';
import sanityClient from './sanityClient';
import ModelPopup from './ModelPopup';

export default function ModelList() {
  const [models, setModels] = useState([]);
  const [selectedModel, setSelectedModel] = useState(null);

  useEffect(() => {
    sanityClient
      .fetch('*[_type == "threeDModel"]')
      .then((data) => {
        console.log("Fetched models:", data);
        setModels(data);
      })
      .catch((error) => console.error("Error fetching models:", error));
  }, []);

  if (models.length === 0) {
    return <p>No models found.</p>;
  }

  return (
    <div className="model-list">
      {models.map((model) => (
        <div
          key={model._id}
          onClick={() => {
            console.log("Clicked model:", model); // Log click event
            setSelectedModel(model); // Set selected model
          }}
        >
          <h3>{model.title}</h3>
          <img
            src={model.thumbnail?.asset?.url}
            alt={model.title}
            style={{ width: '100px', height: '100px' }}
          />
        </div>
      ))}
      {selectedModel && (
        <ModelPopup
          isOpen={true} // Always set to true for debugging
          onClose={() => {
            console.log("Closing popup"); // Log close event
            setSelectedModel(null);
          }}
          modelTitle={selectedModel.title}
          modelUrl={selectedModel.modelFile?.asset?.url}
        />
      )}
    </div>
  );
}
